<style>
  .xlogo,
  .content-logo {
    transition: all 0.4s ease;
    height: 100%;
  }

  .content-logo .logo-wrapper * {
    transition: all 0.4s;
  }

  .xlogo:hover {
    transform: scale(1.05);
    transition: all 0.4s;
  }

  .xlogo:hover .content-logo .logo-wrapper * {
    stroke-width: 1px !important;
    fill: transparent !important;
  }

  .xlogo {
    cursor: pointer;
  }

  .logo-wrapper {
    padding: 0;
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .logo {
    padding: 0;
    margin: 0;
    display: flex;
    align-items: center;
    transition: all 0.3s ease;
  }
  path {
    stroke-width: 1px;
    stroke-dasharray: 1000;
    stroke-dashoffset: 1000;
    stroke-linecap: round;
    stroke-linejoin: round;
    fill: rgba(62, 62, 62, 0);
    stroke: #3e3e3e;
    animation: logo-animation 5s cubic-bezier(0.34, 0, 0.62, 0.15) both;
  }
  svg {
    width: 100%;
    height: 100%;
  }
  @keyframes logo-animation {
    0% {
      stroke-opacity: 0.6;
    }
    15% {
      fill: rgba(62, 62, 62, 0);
    }
    55% {
      stroke-dashoffset: 0;
      stroke-opacity: 1;
    }
    100% {
      fill: var(--two);
      stroke-dashoffset: 0;
      stroke-width: 0;
    }
  }
</style>
